  SELECT time from pantry.last_kiosk_status WHERE kiosk_id = new.kiosk_id
         INTO last_kiosk_status_time;
  status_is_newer =  (last_kiosk_status_time is null) or new.time > last_kiosk_status_time;

  IF status_is_newer
    THEN
